import React, { useState, useEffect } from 'react'

import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Badge from 'react-bootstrap/Badge';
import Table from 'react-bootstrap/Table';

function Files({ showFilesModal, setFilesModuleFromChild }) {
    const [files, setFiles] = useState([])

    useEffect(() => {
        fetch("http://localhost:5000/show_files", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(files => {setFiles(files)})
    }, [])

    // const sendCredentials = () => {
    //     fetch("http://localhost:5000/update_credentials", {
    //         method: 'POST',
    //         headers: {'Content-Type': 'application/json'},
    //         mode: 'cors',
    //         body: JSON.stringify({"credentials": input})
    //     })
    // }
  return (
    <Modal show={showFilesModal} onHide={() => {setFilesModuleFromChild(false)}}>
        <Modal.Header closeButton>
            <Modal.Title>Files API credentials</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Table striped>
            <thead>
                <tr>
                    <th>Date</th>
                    <th>Current</th>
                    <th>Albums num</th>
                    <th>Genres</th>
                    <th>Sort</th>
                    <th>Spotify</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
            {files.map((f, i) => (
                    <tr>
                        <td key={i}>{f.fetch_date}</td>
                        <td key={i}>{f.is_current}</td>
                        <td key={i}>{f.items}</td>
                        <td key={i}>{f.genres}</td>
                        <td key={i}>{f.sort}</td>
                        <td key={i}>{f.spotify_search}</td>
                        {(f.is_current === "1") ? (
                            <td>
                                <Badge bg="danger">Delete</Badge>
                            </td>
                        ): (
                            <td>
                                <Badge bg="danger" disabled>Delete</Badge>
                            </td>
                        )} 

                    </tr>
            ))}
            </tbody>
        </Table>
        </Modal.Body>
        <Modal.Footer>

                <Button variant="primary" onClick={() => {setFilesModuleFromChild(false)}}>Close</Button>
        </Modal.Footer>
    </Modal>
  )
}

export default Files
