import React, { useState, useEffect } from 'react'
import { Form, Box, Button, Block, Notification } from 'react-bulma-components';

// pobieranie slownika gatunkow

function Filters({ albums }) {
    const { Input, Field, Control, Label, Select, Help } = Form
    const [filters, setFilters] = useState({
        byReleaseVal: '',
        byReleaseItems: [824995770, 2728535532] //
    })

    useEffect(() => {
        for (let i = 0; i < albums.length; i ++) {
            if (filters.byReleaseItems.indexOf(albums[i].tralbum_id) > -1) {
                document.getElementById(albums[i].tralbum_id).style.display = 'none'
            } else {
                document.getElementById(albums[i].tralbum_id).style.display = ''
            }
        }

    }, [])

    const handleInput = (e) => {
        const keyName = e.target.name
        const keyVal = e.target.value
        setFilters(prev => ({...prev, [keyName]: keyVal}))
      }

  return (
    <Field horizontal>
    <Field.Label>
        <Label>Genres</Label>
    </Field.Label>
    <Field.Body>
        <Field>
            <Control>
                <Input 
                    type='text'
                    name='byReleaseItems'
                    value={filters.byReleaseItems}
                    onChange={handleInput}
                    placeholder='ambient, thrash metal, etc.'/>
            </Control>
        </Field>
    </Field.Body>
</Field>
  )
}

export default Filters
