// React library
import React, { useState, useEffect } from 'react';

// Bootstrap library
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Row from 'react-bootstrap/Row';

// Custom library
import Feed from './components/Feed/Feed';
import Search from './components/Search/Search';
import Filters from './components/Filters/Filters';
import Spotify from './components/Spotify/Spotify';
import Files from './components/Files/Files';


// TODO(): Likes, save - user library
function App() {
    const [feed, setFeed] = useState([])
    const [real, setReal] = useState([])
    const [modals, setModals] = useState({
        search: false,
        spotify: false,
        files: false,
        filters: false
    })
    // const [showSearchModal, setSearchModal] = useState(false)
    // const [showSpotifyModal, setSpotifyModal] = useState(false)
    // const [showFilesModal, setFilesModal] = useState(false)
    // const [showFilters, setShowFilters] = useState(false)

    const setFromChild = (r, child) => {
        if (child === 'real') {
            setReal(r)
        } else {
            setModals(prev => ({...prev, [child]: r}))
        }
    }

    // const setRealFromChild = (r) => {
    //     setReal(r)
    // }

    // const setSearchModuleFromChild = (r) => {
    //     setSearchModal(r)
    // }

    // const setSpotifyModuleFromChild = (r) => {
    //     setSpotifyModal(r)
    // }

    // const setFilesModuleFromChild = (r) => {
    //     setFilesModal(r)
    // }

    useEffect(() => {
        // TODO(): Refresh after search
        fetch("http://localhost:5000/send_feed", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(feed => {
                setFeed(feed)
                setReal(feed)
            })
    }, [])

    return (
        <Container fluid>
            {/* Modals */}
            <Search showModal={modals.search} changeShow={setFromChild}/>
            <Spotify showSpotifyModal={showSpotifyModal} setSpotifyModuleFromChild={setSpotifyModuleFromChild}/>
            <Files showFilesModal={showFilesModal} setFilesModuleFromChild={setFilesModuleFromChild}/>

            <Row>
                <Navbar bg="dark" variant="dark" className="justify-content-center">
                    <Navbar.Brand>BANDSCRAP2R</Navbar.Brand>
                    <Nav style={{ fontWeight: '500', fontSize: 'x-large' }}>
                        <Nav.Item>
                            <Nav.Link onClick={() => {setSearchModal(true)}}>New Search</Nav.Link>
                        </Nav.Item>

                        <Nav.Item>
                            <Nav.Link onClick={() => {setFilesModal(true)}}>Files</Nav.Link>
                        </Nav.Item>

                        <Nav.Item>
                            <Nav.Link onClick={() => {setSpotifyModal(true)}}>Spotify credentials</Nav.Link>
                        </Nav.Item>

                        <Nav.Item>
                            <Nav.Link onClick={() => {setShowFilters(!showFilters)}}>Filters</Nav.Link>
                        </Nav.Item>
                    </Nav>
                </Navbar>
                </Row>  


                {(showFilters) ? (
                    <Row>
                        <Filters albums={feed} setRealFromChild={setRealFromChild} />  
                    </Row> 
                ) : (
                    <Row></Row>
                )}


                {(real.length === 0 & feed.length === 0) ? (
                        <p>Use search option</p>

                ) : (
                    (real.length > 0) ? (

                        <Row className="g-4 justify-content-md-center" style={{ margin: '20px 90px' }}>
                            <Feed albums={real} />
                        </Row>    

                    ): (
                        <p>Nothing to show</p>
                    )   
                )}
        </Container>

    )
}

export default App
