import React, { useState } from 'react';

import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';

function Feed({ albums }) {
    const [moreInfo, setMoreInfo] = useState({tralbum_id: 0})

    const getMoreInfo = (albumID) => {
        if (albumID > 0) {
            fetch(`http://localhost:5000/details/${albumID}`, {
                method: 'GET',
                headers: {'Content-Type': 'application/json'}
            }).then(res => res.json()).then(data => {
                setMoreInfo({
                    tralbum_id: data[0].tralbum_id,
                    album_description: data[0].album_description,
                    tracks_num: data[0].tracks_num
                })
            })
        }
    }

  return (
        [albums.map((album, i) => (
            <Col>
                <Card key={album.tralbum_id} id={album.tralbum_id} style={{ width: '403px' }} border="dark">
                    {/* <iframe style={{border: "0", width: "400px", height: "400px"}} src={`https://bandcamp.com/EmbeddedPlayer/album=${album.tralbum_id}/size=large/bgcol=ffffff/linkcol=0687f5/minimal=true/track=c/transparent=true/`} seamless>
                    </iframe>  */}
                    <Card.Body>
                        <Card.Title>{album.title}</Card.Title>
                        <Card.Subtitle className="mb-2 text-muted">By: {album.artist}</Card.Subtitle>
                        <Card.Text style={{ height: "250px", overflow: "scroll" }}>
                            {/* TODO(): Links to bandcamp and spotify + icons */}
                            <p>Relese date: {album.published}, 
                            {(album.is_preorder == '1') ? (
                                <span> preorder</span>
                            ) : (
                                <span> buynow</span>
                            )}</p>

                            <p>Label: {album.band_name}, {album.label_origin}</p>
                            <p>Price: {album.price} {album.currency}</p>
                            <p>Genres: {album.genres.split(',').map((genre, j) => (
                                (j < album.genres.split(',').length -1) ? (
                                    <span key={j}>{genre.replace(/\]|\[|\'/g, '')},</span>
                                ) : (
                                    <span key={j}>{genre.replace(/\]|\[|\'/g, '')}</span>
                                )
                            ))}</p>
                            {(moreInfo.tralbum_id == album.tralbum_id) ? (
                                    <div>
                                        <Button variant="primary" onClick={() => {setMoreInfo({tralbum_id: 0})}}>Hide info</Button>
                                        <p>Tracks: {moreInfo.tracks_num}</p>
                                        <p>Description: {moreInfo.album_description}</p>
                                    </div>
                                ) : (
                                    <Button variant="primary" onClick={() => {getMoreInfo(album.tralbum_id)}}>More info</Button>
                                )}
                        </Card.Text>
                    </Card.Body>
                    <Card.Footer>
                    <Button variant="primary" onClick={() => {setMoreInfo({tralbum_id: 0})}}>Hide info</Button>
                    </Card.Footer>
                </Card>
            </Col>
        ))]
  )
}

export default Feed
