import React, { useState, useEffect } from 'react'
import { Form, Box, Column } from 'react-bulma-components';
import FiltersLibrary from './FiltersLibrary'

// pobieranie slownika gatunkow

function Filters({ albums, setRealFromChild }) {
    const { Input, Field, Control, Label, Select, Help } = Form
    const [filters, setFilters] = useState({
        artist: '',
        title: '',
        genres: '',
        band_name: '',
        label_origin: ''
    })

    useEffect(() => {
        var f = new FiltersLibrary(albums, filters)
        var match = f.manage()

        function removeDuplicates(arr) {
            return arr.filter((item, 
                index) => arr.indexOf(item) === index);
        }

        setRealFromChild(removeDuplicates(match))
    }, [filters])

    const handleInput = (e) => {
        const keyName = e.target.name
        const keyVal = e.target.value
        setFilters(prev => ({...prev, [keyName]: keyVal}))
      }

  return (
    <Coulumns>
        <Columns.Column size={4}>
            <h1>Runnin Filters</h1>
        </Columns.Column>
        <Columns.Column>
            <Box>
                <h1>Phrase search</h1>
                {/* Search by key - artist */}
                <Field>
                    <Label>Artist name</Label>
                    <Control>
                        <Input 
                            type='text'
                            name='artist'
                            value={filters.artist}
                            onChange={handleInput}
                            placeholder='Search artist name'/>
                    </Control>
                    <Help>
                        It is common on Bandcamp for label to put their name as artist name.
                        In this case try to search artist name also in album's title.
                    </Help>
                </Field>

                {/* Search by key - album title */}
                <Field>
                    <Label>Title</Label>
                    <Control>
                        <Input 
                            type='text'
                            name='title'
                            value={filters.title}
                            onChange={handleInput}
                            placeholder='Search album title'/>
                    </Control>
                    <Help>It is common for label to put artist's name in album's title field.</Help>
                </Field>

                {/* Search by key - genres */}
                <Field>
                    <Label>Genres</Label>
                        <Control>
                            <Input 
                                type='text'
                                name='genres'
                                value={filters.genres}
                                onChange={handleInput}
                                placeholder='Search genres'/>
                        </Control>
                        <Help>Simplified search by key value.</Help>
                </Field>

                {/* Search by key - label */}
                <Field>
                    <Label>Label name</Label>
                    <Control>
                        <Input 
                            type='text'
                            name='band_name'
                            value={filters.band_name}
                            onChange={handleInput}
                            placeholder='Search label'/>
                    </Control>
                    <Help>If an artist doesn't have label, Bandcamp assigns artist name to that field.</Help>
                </Field>

                {/* Search by key - label origin */}
                <Field>
                    <Label>Label origin</Label>
                    <Control>
                        <Input 
                            type='text'
                            name='label_origin'
                            value={filters.label_origin}
                            onChange={handleInput}
                            placeholder='Search in label origin'/>
                    </Control>
                </Field>
            </Box>
        </Columns.Column>
    </Coulumns>

  )
}

export default Filters
