import React, { useState, useEffect } from 'react'

import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';

function Files({ showModal, changeShow }) {
    const [helpMsg, setHelpMsg] = useState('')
    const [files, setFiles] = useState([])

    useEffect(() => {
        fetch("http://localhost:5000/show_files", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(files => {setFiles(files)})
    }, [])

    const fileManager = (operation, sessionID) => {
        fetch(`http://localhost:5000/${operation}/${sessionID}`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*'},
            mode: 'cors'
        })
    }

  return (
    // TODO(): Refresh
    <Modal show={showModal} size="lg" onHide={() => {changeShow(false, 'files')}}>
        <Modal.Header closeButton>
            <Modal.Title>Files API credentials</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            {/* HELP MSG */}
            {(helpMsg.length > 0) ? (
                <Alert variant="success">
                        <p>{helpMsg}</p>
                </Alert>
            ): (
                null
            )}
        <Table striped>
            <thead>
                <tr>
                    <th>Date</th>
                    <th>Current</th>
                    <th>Albums num</th>
                    <th>Genres</th>
                    <th>Sort</th>
                    <th>Spotify</th>
                    <th>Saved</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
            {files.map((f, i) => (
                <tr>
                    <td key={i}>{f.fetch_date}</td>
                    <td key={i}>{f.is_current}</td>
                    <td key={i}>{f.items}</td>
                    <td key={i}>{f.genres}</td>
                    <td key={i}>{f.sort}</td>
                    <td key={i}>{f.spotify_search}</td>
                    <td key={i}>{f.is_saved}</td>
                    {(f.is_current === 1) ? (
                        <td>
                            <Button variant="primary" onClick={() => {
                                fileManager('save', f.fetch_id)
                                setHelpMsg('Search saved.')
                                }}>Save</Button>
                        </td>
                    ): (
                        <td>
                            <Button variant="primary" onClick={() => {
                                fileManager('delete', f.fetch_id)
                                setHelpMsg('File deleted.')
                                }}>Delete</Button>
                            <Button variant="primary" onClick={() => {
                                fileManager('load', f.fetch_id)
                                setHelpMsg('File loaded.')
                                }}>Load</Button>
                        </td>
                    )} 

                </tr>
            ))}
            </tbody>
        </Table>
        </Modal.Body>
        <Modal.Footer>
            <Button variant="primary" onClick={() => {changeShow(false, 'files')}}>Close</Button>
        </Modal.Footer>
    </Modal>
  )
}

export default Files
