import React, { useState, useEffect } from 'react';
import { Box, Heading, Button, Columns } from 'react-bulma-components';

function Feed({ albums }) {
    const [moreInfo, setMoreInfo] = useState({tralbum_id: 0})

    const getMoreInfo = (albumID) => {
        if (albumID > 0) {
            fetch(`http://localhost:5000/details/${albumID}`, {
                method: 'GET',
                headers: {'Content-Type': 'application/json'}
            }).then(res => res.json()).then(data => {
                setMoreInfo({
                    tralbum_id: data[0].tralbum_id,
                    band_name: data[0].band_name,
                    label_origin: data[0].label_origin,
                    album_description: data[0].album_description,
                    price: data[0].price,
                    currency: data[0].currency,
                    tracks_num: data[0].tracks_num
                })
            })
        }
    }

  return (
        [albums.map((album, i) => (
            <Box key={album.tralbum_id} id={album.tralbum_id}>
                <Columns>
                <Columns.Column narrow={true}>
                    <iframe style={{border: "0", width: "200px", height: "200px"}}
                            src={`https://bandcamp.com/EmbeddedPlayer/album=${album.tralbum_id}/size=large/bgcol=ffffff/linkcol=0687f5/minimal=true/track=c/transparent=true/`} seamless>
                    </iframe>
                </Columns.Column>
                    <Columns.Column>
                        <Heading size={4}>{album.title}</Heading>
                        <Heading subtitle={true}>By: {album.artist}</Heading>
                        <p>Relese date: {album.published}, 
                        {(album.is_preorder == '1') ? (
                            <span> preorder</span>
                        ) : (
                            <span> buynow</span>
                        )}</p>
                        <p>Genres: {album.genres.split(',').map((genre, j) => (
                            (j < album.genres.split(',').length -1) ? (
                                <span key={j}>{genre.replace(/\]|\[|\'/g, '')},</span>
                            ) : (
                                <span key={j}>{genre.replace(/\]|\[|\'/g, '')}</span>
                            )
                        ))}</p>
                        {(moreInfo.tralbum_id == album.tralbum_id) ? (
                            <div>
                                <Button onClick={() => {setMoreInfo({tralbum_id: 0})}}>Hide info</Button>
                                <p>Label: {moreInfo.band_name}, {moreInfo.label_origin}</p>
                                <p>Price: {moreInfo.price} {moreInfo.currency}</p>
                                <p>Tracks: {moreInfo.tracks_num}</p>
                                <p>Description: {moreInfo.album_description}</p>
                            </div>
                        ) : (
                            <Button onClick={() => {getMoreInfo(album.tralbum_id)}}>More info</Button>
                        )}
                    </Columns.Column>
                </Columns>
            </Box>
        ))]
  )
}

export default Feed
