import React, { useState, useEffect } from 'react'

import Row from 'react-bootstrap/Row';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import Form from 'react-bootstrap/Form';

import FiltersLibrary from './FiltersLibrary'

// pobieranie slownika gatunkow

function Filters({ albums, setRealFromChild }) {
    const [filters, setFilters] = useState({
        artist: '',
        title: '',
        genres: '',
        band_name: '',
        label_origin: ''
    })

    useEffect(() => {
        var f = new FiltersLibrary(albums, filters)
        var match = f.manage()

        function removeDuplicates(arr) {
            return arr.filter((item, 
                index) => arr.indexOf(item) === index);
        }

        setRealFromChild(removeDuplicates(match))
    }, [filters])

    const handleInput = (e) => {
        const keyName = e.target.name
        const keyVal = e.target.value
        setFilters(prev => ({...prev, [keyName]: keyVal}))
      }

  return (
    <Tabs defaultActiveKey="phrase" id="filters-menu" className="mb-3">

        <Tab eventKey="phrase" title="Phrase search">
            <Form>
                {/* Search by phrase - artist name */}
                <Form.Group>
                    <Form.Label>Artist name</Form.Label>
                    <Form.Control type="text" placeholder="Artist name" name='artist' value={filters.artist} onChange={handleInput}/>
                    <Form.Text className="text-muted">
                        It is common on Bandcamp for label to put their name as artist name.
                        In this case try to search artist name also in album's title.
                    </Form.Text>
                </Form.Group>

                {/* Search by phrase - album title */}
                <Form.Group>
                    <Form.Label>Album title</Form.Label>
                    <Form.Control type="text" placeholder="Album title" name='title' value={filters.title} onChange={handleInput}/>
                    <Form.Text className="text-muted">
                        It is common for label to put artist's name in album's title field.
                    </Form.Text>
                </Form.Group>
            </Form>
        </Tab>

