// React library
import React, { useState, useEffect } from 'react';

// Bootstrap library
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Row from 'react-bootstrap/Row';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

// Custom library
import Feed from './components/Feed/Feed';
import Search from './components/Search/Search';
import Filters from './components/Filters/Filters';



function App() {
    const [feed, setFeed] = useState([])
    const [real, setReal] = useState([])
    const [searchModal, setSearchModal] = useState(false)

    const setRealFromChild = (r) => {
        setReal(r)
    }

    useEffect(() => {
        fetch("http://localhost:5000/send_feed", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(feed => {
                setFeed(feed)
                setReal(feed)
            })
    }, [])

    return (
        <Container>
            {/* Modals */}
            <Modal show={searchModal} onHide={setSearchModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>New search</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Search />
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary">Search</Button>
                    <Button variant="primary">Close</Button>
                </Modal.Footer>
            </Modal>

            <Row>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand>BANDSCRAP2R</Navbar.Brand>
                    <Nav>
                        <Nav.Link onClick={() => {setSearchModal(true)}}>New Search</Nav.Link>
                        <Nav.Link>Files</Nav.Link>
                    </Nav>
                </Navbar>
            </Row>
            <Row>
                <Filters albums={feed} setRealFromChild={setRealFromChild} />
            </Row>                   
                {(real.length === 0 & feed.length === 0) ? (
                        <p>Use search option</p>

                ) : (
                    (real.length > 0) ? (
                        <Row className="g-4">
                            <Feed albums={real} />
                        </Row>    
                    ): (
                        <p>Nothing to show</p>
                    )   
                )}
        </Container>

    )
}

export default App
