import React, { useState, useEffect } from 'react'

import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';

function Files({ showModal, changeShow }) {
    const [files, setFiles] = useState([])

    useEffect(() => {
        fetch("http://localhost:5000/show_files", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(files => {setFiles(files)})
    }, [])

    const fileManager = (operation, sessionID) => {
        fetch(`http://localhost:5000/${operation}/${sessionID}`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json', 'Access-Control-Allow-Origin': 'origin-list'},
            mode: 'cors'
        })
    }

    const saveSession = (sessionid) => {
        fetch(`http://localhost:5000/save/${sessionid}`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json', 'Access-Control-Allow-Origin': 'origin-list'},
            mode: 'cors'
        })
    }

    const deleteSession = (sessionid) => {
        fetch(`http://localhost:5000/delete/${sessionid}`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json', 'Access-Control-Allow-Origin': 'origin-list'},
            mode: 'cors'
        })
    }

    const loadSession = (sessionid) => {
        fetch(`http://localhost:5000/load/${sessionid}`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json', 'Access-Control-Allow-Origin': 'origin-list'},
            mode: 'cors'
        })
    }
  return (
    <Modal show={showModal} onHide={() => {changeShow(false, 'files')}}>
        <Modal.Header closeButton>
            <Modal.Title>Files API credentials</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Table striped>
            <thead>
                <tr>
                    <th>Date</th>
                    <th>Current</th>
                    <th>Albums num</th>
                    <th>Genres</th>
                    <th>Sort</th>
                    <th>Spotify</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
            {files.map((f, i) => (
                    <tr>
                        <td key={i}>{f.fetch_date}</td>
                        <td key={i}>{f.is_current}</td>
                        <td key={i}>{f.items}</td>
                        <td key={i}>{f.genres}</td>
                        <td key={i}>{f.sort}</td>
                        <td key={i}>{f.spotify_search}</td>
                        <td key={i}>{f.is_saved}</td>
                        {(f.is_current === 1) ? (
                            <td>
                                <Button variant="primary" onClick={() => {fileManager('save', f.fetch_id)}}>Save</Button>
                            </td>
                        ): (
                            <td>
                                <Button variant="primary" onClick={() => {fileManager('delete', f.fetch_id)}}>Delete</Button>
                                <Button variant="primary" onClick={() => {fileManager('load', f.fetch_id)}}>Load</Button>
                            </td>
                        )} 

                    </tr>
            ))}
            </tbody>
        </Table>
        </Modal.Body>
        <Modal.Footer>

                <Button variant="primary" onClick={() => {changeShow(false, 'files')}}>Close</Button>
        </Modal.Footer>
    </Modal>
  )
}

export default Files
