class FiltersLibrary {
    constructor(albums, filterDict) {
        this.albums = albums
        this.filterDict = filterDict
        this.match = []
    }

    // Filter by given value in specified fields
    byKey(dictParam, fieldsToSearch) {
        console.log(dictParam)
        console.log(fieldsToSearch)
        var searchVal = this.filterDict[dictParam]
        for (let a of this.albums) {
            fieldsToSearch.forEach((field) => {
                if (a[field].toLowerCase().includes(searchVal)) {
                    this.match.push(a)
                }
            })
        }
    }

    byDate(dictParam, mode) {
        var searchVal = this.filterDict[dictParam]
        for (let a of this.albums) {
            if ((new Date(a.published) > new Date(searchVal) && (dictParam === 'byDateStart'))) {
                this.match.push(a)
            } else if ((new Date(a.published) < new Date(searchVal) && (dictParam === 'byDateEnd'))) {
                this.match.push(a)
            }
        }
    }

    manage() {
        for (let key in this.filterDict) {
            var keySearch = ['artist', 'title', 'genres', 'band_name', 'album_description', 'label_origin']
            if (this.filterDict[key].length > 0) {
                switch(key) {
                    case keySearch[0]:
                    case keySearch[1]:
                    case keySearch[2]:
                    case keySearch[3]:
                    case keySearch[4]:
                    case keySearch[5]:
                        this.byKey(key, [key])
                        break
                    case 'byKey':
                        this.byKey(key, keySearch)
                        break
                    case 'byDateStart':
                    case 'byDateEnd':
                        this.byDate(key)
                        break
                }
            }
        }
        var toReturn = new Set(this.match)
        return toReturn
    }
}

export default FiltersLibrary
