// React library
import React, { useState, useEffect } from 'react';

// Bootstrap library
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Row from 'react-bootstrap/Row';

// Custom library
import Feed from './components/Feed/Feed';
import Search from './components/Search/Search';
import Filters from './components/Filters/Filters';
import Spotify from './components/Filters/Spotify';


// TODO(): Likes, save - user library
function App() {
    const [feed, setFeed] = useState([])
    const [real, setReal] = useState([])
    const [showSearchModal, setSearchModal] = useState(false)
    const [showSpotifyModal, setSpotifyModal] = useState(false)

    const setRealFromChild = (r) => {
        setReal(r)
    }

    const setSearchModuleFromChild = (r) => {
        setSearchModal(r)
    }

    const setSpotifyModuleFromChild = (r) => {
        setSpotifyModal(r)
    }

    useEffect(() => {
        // TODO(): Refresh after search
        fetch("http://localhost:5000/send_feed", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(feed => {
                setFeed(feed)
                setReal(feed)
            })
    }, [])

    return (
        <Container>
            {/* Modals */}
            <Search showSearchModal={showSearchModal} setSearchModuleFromChild={setSearchModuleFromChild}/>
            <Spotify showSpotifyModal={showSpotifyModal} setSpotifyModuleFromChild={setSpotifyModuleFromChild}/>

            <Row>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand>BANDSCRAP2R</Navbar.Brand>
                    <Nav>
                        <Nav.Link onClick={() => {setSearchModal(true)}}>New Search</Nav.Link>
                        <Nav.Link>Session</Nav.Link>
                        <Nav.Link>Spotify credentials</Nav.Link>
                    </Nav>
                </Navbar>
            </Row>
            <Row>
                <Filters albums={feed} setRealFromChild={setRealFromChild} />
            </Row>                   
                {(real.length === 0 & feed.length === 0) ? (
                        <p>Use search option</p>

                ) : (
                    (real.length > 0) ? (
                        <Row className="g-4">
                            <Feed albums={real} />
                        </Row>    
                    ): (
                        <p>Nothing to show</p>
                    )   
                )}
        </Container>

    )
}

export default App
