import React, { useState, useEffect } from 'react';
import { Box, Heading, Button } from 'react-bulma-components';

function Feed({ albums }) {
    for (let i = 0; i < albums.length; i++) {
        let gArray = albums[i].genres.split(',').map((g) => (g.replace(/\]|\[|\'/g, '')))
        albums[i].genres = gArray
    }
    console.log(albums)
  return (
        [albums.map((album, i) => (
            <Box key={album.tralbum_id} id={album.tralbum_id}>
                <Heading size={4}>{album.title}</Heading>
                <Heading subtitle={true}>By: {album.artist}</Heading>
                <p>Relese date: {album.published} 
                {(album.is_preorder == '1') ? (<span> preorder</span>) : (<span> buynow</span>)}</p>
                <p>Genres: {album.genres.split(',').map((genre, j) => (
                    <span key={j}>{genre.replace(/\]|\[|\'/g, '')},</span>
                ))}</p>
            </Box>
        ))]
  )
}

export default Feed
